CREATE TABLE users (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT NOT NULL, email TEXT UNIQUE NOT NULL); CREATE TABLE orders (id INTEGER PRIMARY KEY AUTOINCREMENT, user_id INTEGER NOT NULL, product TEXT NOT NULL, amount REAL NOT NULL, FOREIGN KEY (user_id) REFERENCES users(id)); INSERT INTO users (name, email) VALUES ('Alice', 'alice@example.com'), ('Bob', 'bob@example.com'); INSERT INTO orders (user_id, product, amount) VALUES (1, 'Laptop', 1299.99), (1, 'Mouse', 25.50), (2, 'Keyboard', 75.00);
